mapper.select:
SELECT CONCAT( u.`nick_name`, '(', u.`user_name`, ')' ) uniqueName, IFNULL(t1.give, 0) give, IFNULL(t2.gain, 0) gain, IFNULL(t2.praise_to, 0) praise_to, IFNULL(t1.praise_from, 0) praise_from, u.user_name as username FROM users u LEFT JOIN (SELECT COUNT(1) give, p.`praise_from` FROM `praise` p
AND p.create_date >= #{praiseDateBegin}
AND p.create_date < DATE_ADD( STR_TO_DATE(#{praiseDateEnd}, '%Y-%m-%d %H'), INTERVAL 1 DAY )
GROUP BY p.`praise_from`) t1 ON t1.praise_from = u.`user_name` LEFT JOIN (SELECT COUNT(1) gain, p.`praise_to` FROM `praise` p
AND p.create_date >= #{praiseDateBegin}
AND p.create_date < DATE_ADD( STR_TO_DATE(#{praiseDateEnd}, '%Y-%m-%d %H'), INTERVAL 1 DAY )
GROUP BY p.`praise_to`) t2 ON t2.praise_to = u.`user_name`
AND u.enable_flag = 1
AND u.user_name = #{userName}
ORDER BY t2.gain DESC

SELECT u.`id`, u.`user_name` AS userName, u.`nick_name` AS nickName, u.`portrait`, u.`signature`, u.`sex`, p.`content`, p.`create_date` createDate, p.`praise_id` praiseId FROM users u JOIN `praise` p
ON u.`user_name` = p.`praise_from`
ON u.`user_name` = p.`praise_to`
AND p.create_date >= #{praiseDateBegin}
AND p.create_date < DATE_ADD( STR_TO_DATE(#{praiseDateEnd}, '%Y-%m-%d %H'), INTERVAL 1 DAY )
AND p.praise_to = #{praiseTo}
AND p.praise_from = #{praiseFrom}

SELECT praise_to AS username FROM(SELECT praise_to,COUNT(1) FROM praise GROUP BY praise_to ORDER BY COUNT(1) DESC )t1 LIMIT 0,1 union SELECT praise_from AS username FROM(SELECT praise_from,COUNT(1) FROM praise GROUP BY praise_from ORDER BY COUNT(1) DESC )t1 LIMIT 0,1 union SELECT praise_to AS username FROM (SELECT praise_to, COUNT(1) FROM praise where praise_to = #{userName} GROUP BY praise_to ORDER BY COUNT(1) DESC) t1 LIMIT 0, 1 union SELECT praise_from AS username FROM (SELECT praise_from, COUNT(1) FROM praise where praise_from = #{userName} GROUP BY praise_from ORDER BY COUNT(1) DESC) t1 LIMIT 0, 1 union SELECT praise_to AS username FROM(SELECT praise_to,COUNT(1) FROM praise where 1=1
GROUP BY praise_to ORDER BY COUNT(1) DESC )t1 LIMIT 0,1 union SELECT praise_from AS username FROM(SELECT praise_from,COUNT(1) FROM praise where 1=1
GROUP BY praise_from ORDER BY COUNT(1) DESC )t1 LIMIT 0,1 union SELECT praise_to AS username FROM (SELECT praise_to, COUNT(1) FROM praise where praise_to = #{userName}
GROUP BY praise_to ORDER BY COUNT(1) DESC) t1 LIMIT 0, 1 union SELECT praise_from AS username FROM (SELECT praise_from, COUNT(1) FROM praise where praise_from = #{userName}
GROUP BY praise_from ORDER BY COUNT(1) DESC) t1 LIMIT 0, 1
